mapper.update:
UPDATE transaction
payment_option = #{paymentOption} , receiver_name = #{receiverName} , receiver_phone = #{receiverPhone} , demailaddr = #{divyDate} , dlvy_request = #{divyRequest} , DLVY_DATE = #{divyDate}
WHERE TRAN_NO = #{tranNo}

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT TRAN_NO , DEMAILADDR , BUYER_ID , PROD_NO , DLVY_DATE , DLVY_REQUEST , ORDER_DATA , PAYMENT_OPTION ,
					RECEIVER_NAME , RECEIVER_PHONE , TRAN_STATUS_CODE 
  					WHERE buyer_id = #{buyer}
  					order by tran_NO DESC
  				) countTable
